	if (formData.DevName === "" || existingNames.indexOf(formData.DevName.toUpperCase()) === -1 || 
		(oldRec.toUpperCase() === selectedRec.toUpperCase() && formData.DevName.toUpperCase() === oldName.toUpperCase())) {
		saveRecs(formData);
	} else {
		Client.invokeModal("OK","Save Device", "Can't save device '"+ selectedRec + "' as device name '" + formData.DevName + "' isn't unique.");
	}
});

// Delete device button 
ClientEvents.subscribe("DevDelRec", function () {
	var formData = Script.getForm("devForm");
	selectedRec = formData.DevSerial;
	if (selectedRec !== "") {
		if (formData.DevModel !== "") {
			Client.status("Deleting device '" + selectedRec + "' (" + formData.DevModel + ")...", "IMPORTANT");
			Client.invokeModal("OK","Confirm Delete", "Please confirm that you want to delete device '"+ selectedRec + "' (" + formData.DevModel + ").", "Delete", "DELETE", function (confirm) {
				if (confirm) {
					Client.status("Deleting device '" + selectedRec + "'...");
					Devices.manageDevices("delete", selectedDistrib, selectedModel, function (data) {
