							Client.clearDirtyFlag();
							Client.status("Device '" + selectedRec + "' deleted.", "IMPORTANT");
							// refresh the table
							Devices.manageDevices("read", selectedDistrib, selectedModel, function (data) {
								populateTable(data.value);
							}, {"columns":"*"});
							populateModelFields(modelDefaults);
						} else {
							Client.invokeModal("OK","Delete Record", "Device template '" + selectedRec + "' didn't delete correctly. Please check input.");
