function UnitTests()
{
	//var alertType = "RedAlertExpired";
	var alertType = "AmberAlertExpired";
	var result = [];
    for(var currentDevice in devices)
	{
		var selected = devices[currentDevice];

		 activeData["123"].dataRed = [4,4,4,3,3,3,3,3,4,4,4];
		 activeData["123"].dataAmber = [3,3,3,4,4,2,4,4,4,3,3];

	    if(activeData[parseInt(selected.Sid)].dataRed.length > 0 || activeData[parseInt(selected.Sid)].dataAmber.length > 0)
        {        
            var validateAlert;
            var validateReset ;
            if(alertType === "RedAlertExpired")
            {
                validateAlert = ValidateAlert(activeData[parseInt(selected.Sid)].dataRed, alertType);
                validateReset = ValidateAlertReset(activeData[parseInt(selected.Sid)].dataRed);
            }
            else
            {
                validateAlert = ValidateAlert(activeData[parseInt(selected.Sid)].dataAmber, alertType);
                validateReset = ValidateAlertReset(activeData[parseInt(selected.Sid)].dataAmber);
            }


            if(validateAlert.alertReult)
			{

				if(alertType === "RedAlertExpired" && activeData[parseInt(selected.Sid)].RedCanSend === 1 && validateAlert.AlertType === 3)
				{
                    activeData[parseInt(selected.Sid)].RedCanSend = 0;
				//	result.push(SendNotification(activeData[parseInt(selected.Sid)], 1));
					activeData[parseInt(selected.Sid)].dataRed = [];

				}

				if(alertType === "AmberAlertExpired" && activeData[parseInt(selected.Sid)].AmberCanSend === 1 && validateAlert.AlertType === 4)
				{
                    activeData[parseInt(selected.Sid)].AmberCanSend = 0;

				//	result.push(SendNotification(activeData[parseInt(selected.Sid)], 2));
					activeData[parseInt(selected.Sid)].dataAmber = [];

				}
			}
            else{
                if(alertType === "RedAlertExpired" && activeData[parseInt(selected.Sid)].RedCanSend === 1 && validateAlert.AlertType === 3)
				{

					activeData[parseInt(selected.Sid)].dataRed = [];

				}

				if(alertType === "AmberAlertExpired" && activeData[parseInt(selected.Sid)].AmberCanSend === 1 && validateAlert.AlertType === 4)
				{
 					activeData[parseInt(selected.Sid)].dataAmber = [];

				}
            }

			if(validateReset)
			{
				console.log("Resetting thresholds senders");
 				activeData[parseInt(selected.Sid)].RedCanSend = 1;
				activeData[parseInt(selected.Sid)].AmberCanSend = 1;
                activeData[parseInt(selected.Sid)].dataRed = [];
                activeData[parseInt(selected.Sid)].dataAmber = [];
			}

        }
	}
    return result;
}


function ValidateAlert(data, alertType)
{
	var result ={};
    var sendSignal = true;
	var alert;
	switch(alertType)
	{
		case "RedAlertExpired":
			alert = 3;
			break;
		case "AmberAlertExpired":
			alert = 4;
			break;
	}

	for(var item in data)
	{
	//	console.log(data[item]);
		if(data[item] === 2 )
		{
		   sendSignal = false;
		}
	}
	result.AlertType = alert;
    result.alertReult = sendSignal;
	return result;
}



function ValidateAlertReset(data)
{
	var result = false;
	for(var item in data)
	{
	//	console.log(data[item]);
		if(data[item] === 2)
		{
		   result = true;
		}
	}

	return result;
}

