function clearErrorCodeFlagsPressed() {
	// get the selected device id's from the table
	var deviceTableForm = Script.getFormByKey("devices");
	var selectedRowsCollection = deviceTableForm.selectedRows;
	var selectedRowsData = selectedRowsCollection.data;

	// for each device, set the error code flag to zero and update the database
