	var deviceIds = Object.keys(selectedRowsData);
	for (var i = 0; i < deviceIds.length; i++) {
		var deviceId = deviceIds[i];
		// create the record to save
		var tempRecord = {
			"Id": deviceId,
			"ErrorCodeFlag": 0
		};
		dbVal[deviceId] = tempRecord;
		Database.saveRecord("rodent", "Devices", dbVal, function(eventData){
			console.log("Device id " + deviceId + " updated");
		});
	}
	Devices.deleteAllRows();
	Database.readRecords("rodent", "Devices", function (eventData) {
		Devices.receiveValue(SensaCollection.load(eventData.value));
	});
}

