ClientEvents.subscribe("resetErrorTransactions", function (eventData) {
	// set all the error transactions to active 
	Database.readRecords("rodent", "ErrorTransactions", function (eventData) {
		let data = SensaCollection.load(eventData.value);
		data.forEach((record, pk) => {
			record['IsActive'] = 1;
			let dbVal = {};
			dbVal[pk] = record;
			Database.saveRecordParam("rodent", "ErrorTransactions", dbVal);
		});
	});
	// go through all the devices, check if the error code flag has been set, then check if any error transactions exist. If they don't then need to update
	// the error code flag to 0
	Database.readRecords("rodent", "Devices", function (eventData) {
		console.log("Reading devices");
		var data = SensaCollection.load(eventData.value);
		data.forEach((record1, pk1) => {
			console.log("Checking device: " + pk1);

			// check each device, if the error code flag is set then check if there are actually any error transactions
			// if (record1['ErrorCodeFlag'] == 1) {
				//console.log("Error code flag was set for device: " + pk1);

			// check error transaction database
			Database.readRecords("rodent", "ErrorTransactions", function (readData) {
				console.log("Reading error transactions");
				readData = SensaCollection.load(readData.value);
				var transactionCounter = 0;
				readData.forEach((record2, pk2) => {
					transactionCounter++;
				});
				if (transactionCounter == 0) {
					// if there are no transactions, need to set the error code flag to 0
					console.log("No error transactions for device: " + pk1);
					record1['ErrorCodeFlag'] = 0;
					var tempRecord = {'Id':pk1, 'ErrorCodeFlag': 0};
					var dbVal = {};
					dbVal[pk1] = tempRecord;
					Database.saveRecordParam("rodent", "Devices", dbVal);
					console.log("Error code flag cleared for device: " + pk1);
				} else {
					console.log("Found error transactions for device: " + pk1);
					record1['ErrorCodeFlag'] = 1;
					var tempRecord = {'Id':pk1, 'ErrorCodeFlag': 0};
					var dbVal = {};
					dbVal[pk1] = tempRecord;
					Database.saveRecordParam("rodent", "Devices", dbVal);
					console.log("Error code flag set for device: " + pk1);
				}
			}, {"columns": "Id,DeviceId", "filter": "DeviceId = " + pk1}
		 );
			//}
		});
	});
});

