//# sourceURL=dynamic-script.js
/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
 var productCollection;
 var compProductCollection;
 var currentCompany;

 const productsTable = Script.getWidget("productsTable");
 const compProductsTable = Script.getWidget("companyProductsTable");
 const compDropdown = Script.getWidget("companyDropdown");

Script.on("init", function() {
	Client.setScreenVisible("Edit Product", false);
	Client.setScreenVisible("Edit User Role", false);
	Client.setScreenVisible("Edit Error Codes", false);
	Client.setScreenVisible("Edit Company", false);
	Client.setScreenVisible("Edit Model Type", false);
	Client.setScreenVisible("Edit Model", false);
});

 Script.on('load', function() {
	 Database.readRecords("rodent", "Companies", function(eventData) {
		 let companyCollection = SensaCollection.load(eventData.value);

		 Database.readRecords("rodent", "UserRoles", function(eventData) {
			 let userCollection = SensaCollection.load(eventData.value);
			 let filteredUsers = userCollection.query((record, pk) => {
				 if (record['User'] == Client.getUser()) {
					 return true;
				 }
			 });
			 var userCompanies = filteredUsers.getColumn("CompanyId");

			 let filteredCompanies = companyCollection.query((record, pk) => {
				 if (userCompanies.includes(pk)) {
					 return true;
				 }
			 });

			 let compNames = filteredCompanies.getColumn("Name");
			 let compLoc = filteredCompanies.getColumn("Address");
			 let compIds = filteredCompanies.getColumn("Id");

			 // Get from state if coming back from editing/creating
			 currentCompany = Script.getState("currentCompany");
			 if (currentCompany == null) {
				 currentCompany = compIds[0];
			 }


			 let compObj = {};
			 compIds.map((comp, idx) => {
				 compObj[comp] = compNames[idx] + ", " + compLoc[idx];
			 });

			 Object.keys(compObj).forEach((comp, idx) => {
				 filteredCompanies.set({"Id": comp, "Name": compObj[comp]});
			 });

			 filteredCompanies.renameColumn("Name", "text");
			 filteredCompanies.renameColumn("Id", "value");

			 compDropdown.receiveTextValues(filteredCompanies);
			 compDropdown.receiveValue(compObj[currentCompany]);

			 Database.readRecords("rodent", "CompanyProducts", function(eventData) {
				 compProductCollection = SensaCollection.load(eventData.value);
				 updateTable();
			 });
		 });
	 });

	 Database.readRecords("rodent", "Products", function(eventData) {
		 productCollection = SensaCollection.load(eventData.value);
		 let filteredComps = productCollection.query((record, pk) => {
			 if (record['Status'] == '1'){
				 return true;
			 }
		 });
		 ClientEvents.publish("masterDelRows", "");
		 productsTable.receiveValue(filteredComps);
	 });
 });

 ClientEvents.subscribe("addCompanyProducts", addProductToCompany);
 ClientEvents.subscribe("removeCompanyProducts", removeProductFromCompany);
 ClientEvents.subscribe("compSelected", changeCompany);
 ClientEvents.subscribe("selectCompRow", editProduct);
 ClientEvents.subscribe("newProdIcon", createNewProduct);

 function addProductToCompany() {
	 var formData = Script.getFormByKey("products");
	 var compData = formData.all;
	 compData.addColumn("companyid");

	 var dbReq = {};
	 var compProdCount = Object.keys(compProductCollection.data).length;
	 let today = new Date();
	 today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);

	 compData.forEach(function(kvpair, pk) {
		 var prodObj = {};
		 // Check if product already exists with status = 0
		 let matchingProd = compProductCollection.query((record, pk) => {
			 if ((record['CompanyId'] == Script.getFormByKey("products").compDrop) &&
				 (record['Name'] == kvpair.name) &&
				 (record['Expiry'] == kvpair.expiry) &&
				 (record['WeightPerBlock'] == kvpair.weightperblock) &&
				 (record['Status'] == '0')) {
				 return true;
			 }
		 });
		 if (Object.keys(matchingProd.data).length > 0) {
			 // found match, override
			 prodObj.Id = Object.keys(matchingProd.data)[0];

		 } else {
			 prodObj.Id = `${++compProdCount}`;
		 }
		 prodObj.Name = kvpair.name;
		 prodObj.Owner = kvpair.owner;
		 prodObj.Status = kvpair.status;
		 prodObj.CreatedOn = today;
		 prodObj.UpdatedOn = today;
		 prodObj.CreatedBy = Client.getUser();
		 prodObj.UpdatedBy = Client.getUser();
		 prodObj.CompanyId = formData.compDrop;
		 prodObj.Expiry = kvpair.expiry;
		 prodObj.LowerThreshold = kvpair.lowerthreshold;
		 prodObj.UpperThreshold = kvpair.upperthreshold;
		 prodObj.WeightPerBlock = kvpair.weightperblock;
		 dbReq[prodObj.Id] = prodObj;
	 });
	 Database.saveRecord("rodent", "CompanyProducts", dbReq, () => {
		 Database.readRecords("rodent", "CompanyProducts", function(eventData) {
			 compProductCollection = SensaCollection.load(eventData.value);
			 updateTable();
		 });
	 });
 }

 function updateTable() {
	 let filteredComps = compProductCollection.query((record, pk) => {
		 if ((record['CompanyId'] == currentCompany) &&
			 (record['Status'] == '1')){
			 return true;
		 }
	 });

	 ClientEvents.publish("compDelRows", "");
	 compProductsTable.receiveValue(filteredComps);
 }

 function removeProductFromCompany() {
	 var formData = Script.getFormByKey("products");
	 var ids = formData.company.getColumn("id");
	 for (var i = 0; i < ids.length; i++) {
		 compProductsTable.deleteRows(ids[i]);
		 let req = {};
		 req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
		 Database.updateRecord("rodent", "CompanyProducts", req);
	 }
 }

 async function changeCompany(selected) {
	 currentCompany = selected.value;

	 Database.readRecords("rodent", "CompanyProducts", function(eventData) {
		 compProductCollection = SensaCollection.load(eventData.value);
		 Script.setState("currentCompany", currentCompany);
		 updateTable();
	 });
 }

 function editProduct(eventData) {
	 Script.setState("compProductInfo", eventData.value);
	 Script.setState("modifyType", "edit");
	 Client.jumpToScreen("Edit Product");
 }

 function createNewProduct(eventData) {
	 Script.setState("modifyType", "add");
	 Script.setState("companyId", currentCompany);
	 Client.jumpToScreen("Edit Product");
 }

