 */

/**
 * Initialise script state (run once at startup)
 */
var prodId;
var modifyType;
const prodFormContainer = Script.getWidget("productFormContainer");
const prodSubmitButton = Script.getWidget("prodSubmitButton");

Script.on('load', function() {
	modifyType = Script.getState("modifyType");
	if (!modifyType) {
		Client.jumpToScreen("Manage Products");
	}
	var productData;

	if (modifyType.startsWith("add")) {
		prodFormContainer.setTitle("New Product");
		return;
    } else if (modifyType == "edit") {
		productData = Script.getState("compProductInfo");
    } else if (modifyType == "edit_master") {
		productData = Script.getState("productInfo");	   
    }
	var prodObj = {}
	prodId = productData.getColumn("id")[0];
	prodObj.Name = productData.getColumn("name");
	prodObj.Status = productData.getColumn("status");
	prodObj.WeightPerBlock = productData.getColumn("weightperblock");
	prodObj.Expiry = productData.getColumn("expiry");

	Script.setForm("editProduct", prodObj);
	prodFormContainer.setTitle("Edit Product");
});

ClientEvents.subscribe("finishEditProduct", function() {
	switch (modifyType) {
		case 'edit':
			handleEdit();
			break;
		case 'edit_master':
			handleEditMaster();
			break;
		case 'add':
			handleCreate();
			break;
		case 'add_master':
			handleCreateMaster();
			break;
	}
});

function handleEdit() {
	var formObj = Script.getFormByKey("editProduct");
	if (formObj == undefined) {
		alert("Please fill out all required fields");
		return;
	}

	var dbReq = {};
	var companyProducts = Script.getState("companyProducts");
	formObj.Id = prodId;
	formObj.Status = +formObj.Status;
	let today = new Date();
	today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
	formObj.UpdatedOn = today;
	formObj.UpdatedBy = Client.getUser();
	dbReq[prodId] = formObj;

	Database.updateRecord("rodent", "CompanyProducts", dbReq, () => {
		//Script.setState("currentCompany", formObj.CompanyId);
		Client.jumpToScreen("Manage Products");
	});
}

function handleEditMaster() {
	var formObj = Script.getFormByKey("editProduct");
	if (formObj == undefined) {
		alert("Please fill out all required fields");
		return;
	}

	var dbReq = {};
	formObj.Id = prodId;
	formObj.Status = +formObj.Status;
	let today = new Date();
	today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
	formObj.UpdatedOn = today;
	formObj.UpdatedBy = Client.getUser();
	dbReq[prodId] = formObj;

	Database.updateRecord("rodent", "Products", dbReq, () => {
