/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
var productCollection;
// Default
var status = "Show Active";

const productsTable = Script.getWidget("masterProductsTable");

Script.on('load', function() {

	Database.readRecords("rodent", "Products", function(eventData) {
		productCollection = SensaCollection.load(eventData.value);
		updateTable();
	});
});

ClientEvents.subscribe("productSelected", editProduct);
ClientEvents.subscribe("removeMasterProducts", removeProductFromCompany);
ClientEvents.subscribe("addNewMasterProduct", createNewProduct);
ClientEvents.subscribe("changeActive", (eventData) => {
	status = eventData.value; 
	Database.readRecords("rodent", "Products", function(eventData) {
		productCollection = SensaCollection.load(eventData.value);
		updateTable();
	});
	if (status !== "Show Active") {
		// Do not attempt to delete inactive elements
		Script.disableWidget("deleteMasterProdIcon");
		Script.disableWidget("addMasterProdIcon");
	} else {
		// Re-enable widgets if disabled before
		Script.enableWidget("deleteMasterProdIcon");
		Script.enableWidget("addMasterProdIcon");
	}
});

function editProduct(eventData) {
	Script.setState("productInfo", eventData.value);
	Script.setState("modifyType", "edit_master");
	Client.jumpToScreen("Edit Product");
}

function updateTable() {
	//status = Script.getFormByKey("master").activeSelection == "Show Active" ? '1' : '0';
	let filteredComps = productCollection.query((record, pk) => {
		if (record['Status'] == (status == "Show Active")){
			return true;
		}
	});

	ClientEvents.publish("compDelRows", "");
	var tableComps = filteredComps.filter(["Id", "Name", "Expiry", "WeightPerBlock"]);
	tableComps.setColumns(["Id", "Name", "Expiry", "Weight Per Block"]);
	productsTable.receiveValue(tableComps);
}

function createNewProduct(eventData) {
	Script.setState("modifyType", "add_master");
	Client.jumpToScreen("Edit Product");
}

function removeProductFromCompany() {
	var formData = Script.getFormByKey("master");
	var ids = formData.products.getColumn("id");
	for (var i = 0; i < ids.length; i++) {
		productsTable.deleteRows(ids[i]);
		let req = {};
		req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
		Database.updateRecord("rodent", "Products", req);
	}
}

