/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
var userId;
var modifyType;
var companyId;
var userData;

const userFormContainer = Script.getWidget("userRoleContainer");
const userSubmitButton = Script.getWidget("userRoleSubmit");
const editUserSelect = Script.getWidget("editUserSelect");
const editUserCompSelect = Script.getWidget("editUserCompSelect");
const editUserIsAdmin = Script.getWidget("editUserIsAdmin");

Script.on('load', function() {
	modifyType = Script.getState("modifyType");

	if (modifyType == "add") {
        userFormContainer.setTitle("New User Role");
		//Script.enableWidget("editUserSelect");
		Database.readRecords("Directory", "users", function(eventData) {
			let userCollection = SensaCollection.load(eventData.value);
			editUserSelect.receiveList(userCollection.getColumn("username"));
		});
    } else if (modifyType == "edit") {
        userData = Script.getState("userRoleInfo");
		editUserIsAdmin.receiveValue(userData.getColumn("isadmin")[0]);
		userFormContainer.setTitle("Edit User Role");
    }

	Database.readRecords("rodent", "Companies", function(eventData) {
		let companyCollection = SensaCollection.load(eventData.value);
		Database.readRecords("rodent", "UserRoles", function(eventData) {
			 let userCollection = SensaCollection.load(eventData.value);
			 let filteredUsers = userCollection.query((record, pk) => {
				 if (record['User'] == Client.getUser()) {
					 return true;
				 }
			 });
			 var userCompanies = filteredUsers.getColumn("CompanyId");

			 let filteredCompanies = companyCollection.query((record, pk) => {
				 if (userCompanies.includes(pk)) {
					 return true;
				 }
			 });

			 let compNames = filteredCompanies.getColumn("Name");
			 let compLoc = filteredCompanies.getColumn("Address");
			 let compIds = filteredCompanies.getColumn("Id");

			 companyId = Script.getState("companyId");
			 if (companyId == null) {
				 companyId = compIds[0];
			 }

			 let compObj = {};
			 compIds.map((comp, idx) => {
				 compObj[comp] = compNames[idx] + ", " + compLoc[idx];
			 });

			 Object.keys(compObj).forEach((comp, idx) => {
				 filteredCompanies.set({"Id": comp, "Name": compObj[comp]});
			 });

			 filteredCompanies.renameColumn("Name", "text");
			 filteredCompanies.renameColumn("Id", "value");

			 editUserCompSelect.receiveTextValues(filteredCompanies);
			 editUserCompSelect.receiveValue(filteredCompanies.get(companyId).text);
			if (userData != undefined) {
				editUserSelect.receiveValue(userData.getColumn("user")[0]);
				//Script.disableWidget("editUserSelect");
				userId = userData.getColumn("id")[0];
			}
		 });
	});
});

ClientEvents.subscribe("finishEditUser", function() {
	switch (modifyType) {
		case 'edit':
			handleEdit();
			break;
		case 'add':
			handleCreate();
			break;
	}
});

function handleEdit() {
	var formObj = Script.getFormByKey("editUser");
	if (formObj == undefined) {
		alert("Please fill out all required fields");
		return;
	}
	var dbReq = {};
	formObj.Id = userId;
	let today = new Date();
	today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
	formObj.UpdatedOn = today;
	formObj.UpdatedBy = Client.getUser();
	formObj.IsAdmin = +formObj.IsAdmin;
	dbReq[userId] = formObj;

	Database.updateRecord("rodent", "UserRoles", dbReq, () => {
		Script.setState("currentCompany", formObj.CompanyId);
		// Update the other records with this user to account for the IsAdmin field
		Database.readRecords("rodent", "UserRoles", function (eventData) {
			eventData.value.forEach((record, pk) => {
				if (record.Id != userId) {
					let newReq = {};
					record.IsAdmin = +formObj.IsAdmin;
					newReq[userId] = record;
					Database.updateRecord("rodent", "UserRoles", newReq);
				}
			});
			Client.jumpToScreen("User Roles");
		}, {filter: 
			`User='${userData.getColumn("user")[0]}'`
		   }
	    );
    });
}

function handleCreate() {
	Database.readRecords("rodent", "UserRoles", function(eventData) {
		let userRoleCollection = SensaCollection.load(eventData.value);
		var formObj = Script.getFormByKey("editUser");
		if (formObj == undefined) {
			alert("Please fill out all required fields");
			return;
		}
		var dbReq = {};

		let today = new Date();
		today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
		let id = +userRoleCollection.getColumn("Id").at(-1) + 1;
		formObj.Id = `${id}`;
		formObj.CreatedOn = today;
		formObj.UpdatedOn = today;
		formObj.Owner = Client.getUser();
		formObj.CreatedBy = Client.getUser();
        formObj.UpdatedBy = Client.getUser();
		formObj.IsAdmin = +formObj.IsAdmin;


		dbReq[id] = formObj;
		Database.createRecords("rodent", "UserRoles", dbReq, () => {
             Script.setState("currentCompany", formObj.CompanyId);
			 Database.readRecords("rodent", "UserRoles", function (eventData) {
				eventData.value.forEach((record, pk) => {
					if (record.Id != userId) {
						let newReq = {};
						record.IsAdmin = +formObj.IsAdmin;
						newReq[userId] = record;
						Database.updateRecord("rodent", "UserRoles", newReq);
					}
				});
				Client.jumpToScreen("User Roles");
			}, {filter: 
				`User='${formObj.User}'`
			   }
			);
         });
	});
}

