 */

/**
 * Initialise script state (run once at startup)
 */
const errorCodesTable = Script.getWidget("errorCodesTable");
const troubleTable = Script.getWidget("troubleTable");
const deviceModelDropdown = Script.getWidget("deviceModelDropdown");
const errorColumns = ["Id", "Code Id", "Description", "Troubleshooting Help", "Code Type", "CreatedBy", "UpdatedBy", "CreatedOn", "UpdatedOn", "Out of bounds", "Status", "Model Id"];
const fileColumns = ["Id", "File Location", "CreatedBy", "UpdatedBy", "CreatedAt", "Status"];

var errorCollection;
var troubleFilesCollection;

var status = "Show Active";
var deviceModel;
var deviceModelMap = {};

const deleteCodes = {
    "errorCodesTable": "compDelErrorRows",
    "troubleTable": "compDelFileRows"
}

ClientEvents.subscribe("errorCodeSelected", editErrorCode);
ClientEvents.subscribe("addErrorCode", createNewErrorCode);
ClientEvents.subscribe("troubleFileSelected", editTroubleFile);
ClientEvents.subscribe("addTroubleFile", createNewTroubleFile);
ClientEvents.subscribe("deleteErrorCodes", removeErrorCode);
ClientEvents.subscribe("deleteTroubleFiles", removeTroubleFiles);
ClientEvents.subscribe("deviceModelSelected", setDeviceModel);

ClientEvents.subscribe("changeActiveError", (eventData) => {
    status = eventData.value; 
    Database.readRecords("rodent", "ErrorCodes", function(eventData) {
        errorCollection = SensaCollection.load(eventData.value);
        errorCollection.setColumns(errorColumns);
        updateTable(errorCodesTable, errorCollection);
    });
    if (status !== "Show Active") {
        // Do not attempt to delete inactive elements
        Script.hideWidget("deleteErrorCodeIcon");
    } else {
        // Re-enable widgets if hidden before
        Script.showWidget("deleteErrorCodeIcon");
    }
});

ClientEvents.subscribe("changeActiveFiles", (eventData) => {
    status = eventData.value; 
    Database.readRecords("rodent", "TroubleshootingFiles", function(eventData) {
        troubleFilesCollection = SensaCollection.load(eventData.value);
        troubleFilesCollection.setColumns(fileColumns);
        updateTable(troubleTable, troubleFilesCollection);
    });
    if (status !== "Show Active") {
        // Do not attempt to delete inactive elements
