/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
const modelTypesTable = Script.getWidget("modelTypesTable");

var modelTypesCollection;

var status = "Show Active";

ClientEvents.subscribe("editModalType", editModalType);
ClientEvents.subscribe("addModalType", addModalType);
ClientEvents.subscribe("deleteModalType", deleteModalType);
ClientEvents.subscribe("changeActiveModalType", (eventData) => {
    status = eventData.value; 
    Database.readRecords("rodent", "ModelType", function(eventData) {
        modelTypesCollection = SensaCollection.load(eventData.value);
        updateTable(modelTypesTable, modelTypesCollection);
    });
    if (status !== "Show Active") {
        // Do not attempt to delete inactive elements
        Script.hideWidget("deleteModalTypeIcon");
    } else {
        // Re-enable widgets if hidden before
        Script.showWidget("deleteModalTypeIcon");
    }
});

Script.on('load', function() {
	Database.readRecords("rodent", "ModelType", function(eventData) {
        modelTypesCollection = SensaCollection.load(eventData.value);
		updateTable(modelTypesTable, modelTypesCollection);
    });
});

function updateTable(table, collection) {
    let filteredCollection = collection.query((record, pk) => {
        if (record['Status'] == (status == "Show Active")) {
            return true;
        }
    });
    ClientEvents.publish("clearAllModalTypes", "");
    table.receiveValue(filteredCollection);
}

function editModalType(eventData) {
    Script.setState("modelTypeData", eventData.value);
    Script.setState("modifyType", "edit");
    Client.jumpToScreen("Edit Model Type");
}

function addModalType() {
    Script.setState("modifyType", "add");
    Client.jumpToScreen("Edit Model Type");
}

function deleteModalType() {
    var formData = Script.getFormByKey("modelType");
    var ids = formData.modelType.getColumn("id");
    for (var i = 0; i < ids.length; i++) {
        modelTypesTable.deleteRows(ids[i]);
        let req = {};
        req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
        Database.updateRecord("rodent", "ModelType", req);
    }
}

