 */

/**
 * Initialise script state (run once at startup)
 */
var modelTypeId;
var modifyType;
const modelTypeContainer = Script.getWidget("modelTypeContainer");
const modelTypeSubmitButton = Script.getWidget("modelTypeSubmitButton");

Script.on('load', function() {
    modifyType = Script.getState("modifyType");
    var modelTypeData;
	if (modifyType == "add") {
        modelTypeContainer.setTitle("New Model Type");
        return;
    } else if (modifyType == "edit") {
        modelTypeData = Script.getState("modelTypeData");
    }
    var modelTypeObj = {};
    modelTypeId = modelTypeData.getColumn("id")[0];
    modelTypeObj.Name = modelTypeData.getColumn("name");
    modelTypeObj.Status = modelTypeData.getColumn("status");

    Script.setForm("modalTypeForm", modelTypeObj);
    modelTypeContainer.setTitle("Edit Model Type");
});


ClientEvents.subscribe("finishSubmitModalType", function() {
    switch (modifyType) {
        case 'edit':
            handleEdit();
            break;
        case 'add':
            handleCreate();
            break;
    }
});

function handleEdit() {
    var formObj = Script.getFormByKey("modalTypeForm");
    if (formObj == undefined) {
        alert("Please fill out all required fields");
        return;
    }
    var dbReq = {};
    formObj.Id = modelTypeId;
    formObj.Status = +formObj.Status;
    let today = new Date();
    today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
    formObj.UpdatedOn = today;
    formObj.UpdatedBy = Client.getUser();
    dbReq[modelTypeId] = formObj;
	debugger;
    Database.updateRecord("rodent", "ModelType", dbReq, () => {
        Client.jumpToScreen("Manage Model Types");
    });
}

function handleCreate() {
    Database.readRecords("rodent", "ModelType", function(eventData) {
        var modelTypeCollection = SensaCollection.load(eventData.value);
        var formObj = Script.getFormByKey("modalTypeForm");
        if (formObj == undefined) {
            alert("Please fill out all required fields");
            return;
        }
        var dbReq = {};
        var modelTypeCount = Object.keys(modelTypeCollection.data).length;
        formObj.Id = `${modelTypeCount + 1}`;
        formObj.Status = +formObj.Status;
        let today = new Date();
        today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
        formObj.CreatedOn = new Date().getTime();
        formObj.UpdatedOn =  new Date().getTime();
        formObj.CreatedBy = Client.getUser();
        formObj.UpdatedBy = Client.getUser();
        dbReq[modelTypeCount + 1] = formObj;
		debugger;
        Database.createRecords("rodent", "ModelType", dbReq, () => {
            Client.jumpToScreen("Manage Model Types");
        });
    });
}

