ClientEvents.subscribe("finishEditUser", function() {
	switch (modifyType) {
		case 'edit':
			handleEdit();
			break;
		case 'add':
			handleCreate();
			break;
	}
});

function handleEdit() {
	var formObj = Script.getFormByKey("editUser");
	if (formObj == undefined) {
		alert("Please fill out all required fields");
		return;
	}
	var dbReq = {};
	formObj.Id = userId;
	let today = new Date();
	today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
	formObj.UpdatedOn = today;
	formObj.UpdatedBy = Client.getUser();
	formObj.IsAdmin = +formObj.IsAdmin;
	formObj.IsPrimary = +formObj.IsPrimary;
	dbReq[userId] = formObj;

	Database.updateRecord("rodent", "UserRoles", dbReq, () => {
		Script.setState("currentCompany", formObj.CompanyId);
		// Update the other records with this user to account for the IsAdmin field
		Database.readRecords("rodent", "UserRoles", function (eventData) {
			eventData.value.forEach((record, pk) => {
				if (record.Id != userId) {
					let newReq = {};
					record.IsAdmin = +formObj.IsAdmin;
					newReq[userId] = record;
					Database.updateRecord("rodent", "UserRoles", newReq);
				}
			});
			Client.jumpToScreen("User Roles");
		}, {filter: 
			`User='${userData.getColumn("user")[0]}'`
		   }
	    );
    });
}

function handleCreate() {
	Database.readRecords("rodent", "UserRoles", function(eventData) {
		let userRoleCollection = SensaCollection.load(eventData.value);
		var formObj = Script.getFormByKey("editUser");
		if (formObj == undefined) {
			alert("Please fill out all required fields");
			return;
		}
		var dbReq = {};

		let today = new Date();
		today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
		let id = +userRoleCollection.getColumn("Id").at(-1) + 1;
		formObj.Id = `${id}`;
		formObj.CreatedOn = today;
		formObj.UpdatedOn = today;
		formObj.Owner = Client.getUser();
		formObj.CreatedBy = Client.getUser();
        formObj.UpdatedBy = Client.getUser();
		formObj.IsAdmin = +formObj.IsAdmin;
		console.log("data before " + formObj.IsPrimary);
		formObj.IsPrimary = +formObj.IsPrimary;

		console.log("data after " + formObj.IsPrimary);

		dbReq[id] = formObj;
		Database.createRecords("rodent", "UserRoles", dbReq, () => {
             Script.setState("currentCompany", formObj.CompanyId);
			 Database.readRecords("rodent", "UserRoles", function (eventData) {
				eventData.value.forEach((record, pk) => {
					if (record.Id != userId) {
						let newReq = {};
						record.IsAdmin = +formObj.IsAdmin;
						newReq[userId] = record;
						Database.updateRecord("rodent", "UserRoles", newReq);
					}
				});
				Client.jumpToScreen("User Roles");
			}, {filter: 
				`User='${formObj.User}'`
			   }
			);
         });
	});
