/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
const errorCodesTable = Script.getWidget("errorCodesTable");
const troubleTable = Script.getWidget("troubleTable");
const deviceModelDropdown = Script.getWidget("deviceModelDropdown");
const errorColumns = ["Id", "Code Id", "Description", "Troubleshooting Help", "Code Type", "CreatedBy", "UpdatedBy", "CreatedOn", "UpdatedOn", "Out of bounds", "Status", "Model Id"];
const fileColumns = ["Id", "File Location", "CreatedBy", "UpdatedBy", "CreatedAt", "Status"];

var errorCollection;
var troubleFilesCollection;

var status = "Show Active";
var deviceModel;
var deviceModelMap = {};

const deleteCodes = {
    "errorCodesTable": "compDelErrorRows",
    "troubleTable": "compDelFileRows"
}

ClientEvents.subscribe("errorCodeSelected", editErrorCode);
ClientEvents.subscribe("addErrorCode", createNewErrorCode);
ClientEvents.subscribe("troubleFileSelected", editTroubleFile);
ClientEvents.subscribe("addTroubleFile", createNewTroubleFile);
ClientEvents.subscribe("deleteErrorCodes", removeErrorCode);
ClientEvents.subscribe("deleteTroubleFiles", removeTroubleFiles);
ClientEvents.subscribe("deviceModelSelected", setDeviceModel);

ClientEvents.subscribe("changeActiveError", (eventData) => {
    status = eventData.value; 
    Database.readRecords("rodent", "ErrorCodes", function(eventData) {
        errorCollection = SensaCollection.load(eventData.value);
        errorCollection.setColumns(errorColumns);
        updateTable(errorCodesTable, errorCollection);
    });
    if (status !== "Show Active") {
        // Do not attempt to delete inactive elements
        Script.hideWidget("deleteErrorCodeIcon");
    } else {
        // Re-enable widgets if hidden before
        Script.showWidget("deleteErrorCodeIcon");
    }
});

ClientEvents.subscribe("changeActiveFiles", (eventData) => {
    status = eventData.value; 
    Database.readRecords("rodent", "TroubleshootingFiles", function(eventData) {
        troubleFilesCollection = SensaCollection.load(eventData.value);
        troubleFilesCollection.setColumns(fileColumns);
        updateTable(troubleTable, troubleFilesCollection);
    });
    if (status !== "Show Active") {
        // Do not attempt to delete inactive elements
        Script.hideWidget("deleteTroubleFilesIcon");
    } else {
        // Re-enable widgets if hidden before
        Script.showWidget("deleteTroubleFilesIcon");
    }
});

Script.on('load', function() {

    Database.readRecords("rodent", "ErrorCodes", function(eventData) {
        errorCollection = SensaCollection.load(eventData.value);
        errorCollection.setColumns(errorColumns);
		Database.readRecords("rodent", "Model", function (eventData) {
			let modelIds = eventData.value.getColumn("Id");
			let modelNames = eventData.value.getColumn("Name");
			modelNames.forEach((key, i) => deviceModelMap[key] = modelIds[i]);

			deviceModelDropdown.receiveList(modelNames);
			deviceModel = modelIds[0];
			Script.setState("errorDeviceModel", deviceModel);
			updateTable(errorCodesTable, errorCollection);
		});
    });

    Database.readRecords("rodent", "TroubleshootingFiles", function(eventData) {
        troubleFilesCollection = SensaCollection.load(eventData.value);
        troubleFilesCollection.setColumns(fileColumns)
        updateTable(troubleTable, troubleFilesCollection);
    });

});

function setDeviceModel(eventData) {

	deviceModel = deviceModelMap[eventData.value];

	updateTable(errorCodesTable, errorCollection);
	Script.setState("errorDeviceModel", deviceModel); 
}

function updateTable(table, collection) {

	console.log(" receive are " + JSON.stringify(collection, null, 4));
    let filteredCollection = collection.query((record, pk) => {
		if (record["Model Id"] == undefined) {
			// Troubleshooting file
			if (record['Status'] == (status == "Show Active")){
				return true;
			}
		} else {
			// Error Code
			if (record['Status'] == (status == "Show Active") && record["Model Id"] == deviceModel){
				return true;
			} 

		}

    });

    ClientEvents.publish(deleteCodes[table._name], "");
    table.receiveValue(filteredCollection);
}

function editErrorCode(eventData) {
    Script.setState("errorData", eventData.value);
    Script.setState("modifyType", "edit");
    Client.jumpToScreen("Edit Error Codes");
}

function editTroubleFile(eventData) {
    Script.setState("fileData", eventData.value);
    Script.setState("modifyType", "edit_file");
    Client.jumpToScreen("Edit Error Codes");
}

function createNewErrorCode() {
    Script.setState("modifyType", "add");
    Client.jumpToScreen("Edit Error Codes");
}

function createNewTroubleFile() {
    Script.setState("modifyType", "add_file");
    Client.jumpToScreen("Edit Error Codes");
}

function removeErrorCode() {
    var formData = Script.getFormByKey("errors");
    var ids = formData.codes.getColumn("id");
    /*for (var i = 0; i < ids.length; i++) {
        errorCodesTable.deleteRows(ids[i]);
        let req = {};
        req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
        Database.updateRecord("rodent", "ErrorCodes", req);
    } */

	if(ids.length === 0) return;

	var option = {"confirmText": "Confirm", "cancelText": "Cancel"};
	Client.confirm("Do you want to delete error code?", "Delete Error Codes", option).then((eventData) => {

		console.log("Return data is  is " + JSON.stringify(eventData, null, 4));

		if(!eventData) return;
		for (var i = 0; i < ids.length; i++) {
			errorCodesTable.deleteRows(ids[i]);
			//let req = {};
			//req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
			//Database.updateRecord("rodent", "Products", req);
			Database.deleteRecord("rodent", "ErrorCodes", "Id", ids[i]);
		}
	});
}

function removeTroubleFiles() {
    var formData = Script.getFormByKey("errors");
    var ids = formData.files.getColumn("id");
   /* for (var i = 0; i < ids.length; i++) {
        troubleTable.deleteRows(ids[i]);
        let req = {};
        req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
        Database.updateRecord("rodent", "TroubleshootingFiles", req);
    } */


	if(ids.length === 0) return;

	var option = {"confirmText": "Confirm", "cancelText": "Cancel"};
	Client.confirm("Do you want to delete trouble shooting file?", "Delete Trouble Shouting File", option).then((eventData) => {

		console.log("Return data is  is " + JSON.stringify(eventData, null, 4));

		if(!eventData) return;
		for (var i = 0; i < ids.length; i++) {
			troubleTable.deleteRows(ids[i]);
			//let req = {};
			//req[{"Id": ids[i], "Status": "0"}] = {"Id": ids[i], "Status": "0"};
			//Database.updateRecord("rodent", "Products", req);
			Database.deleteRecord("rodent", "TroubleshootingFiles", "Id", ids[i]);
		}
	});

}

