/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
var errorId;
var fileId;
var modifyType;
var deviceModelMap = {};
const errorContainer = Script.getWidget("errorContainer");
const errorSubmitButton = Script.getWidget("errorSubmitButton");
const troubleshootinghelp = Script.getWidget("troubleshootinghelp");
const errorModelDropdown = Script.getWidget("errorModelDropdown");
const cancelErrorCode = Script.getWidget("cancelErrorCode");
Script.on('load', function() {
    // hide at start
    hideErrorForm();
    hideFileForm();

	cancelErrorCode.subscribe("pressed", ()=> {
		Client.jumpToScreen("Error Codes");
	});

    modifyType = Script.getState("modifyType");
    if (modifyType == "add_file") {
        errorContainer.setTitle("New Troubleshooting File");
        showFileForm();
        return;
    } else if (modifyType == "edit_file") {
        var fileData = Script.getState("fileData");
        errorContainer.setTitle("Edit Troubleshooting File");

        var fileObj = {};
        fileId = fileData.getColumn("id")[0];
        fileObj.FileLocation = fileData.getColumn("file location");
        fileObj.Status = fileData.getColumn("status");
        Script.setForm("filelocation", fileObj);
        showFileForm();
        return;
    }

    showErrorForm();
    Database.readRecords("rodent", "TroubleshootingFiles", function(eventData) {
        let fileCollection = SensaCollection.load(eventData.value);
        fileCollection = fileCollection.filter(["Id", "FileLocation"]);
        fileCollection.setColumns(["value", "text"])
        troubleshootinghelp.receiveTextValues(fileCollection);

		Database.readRecords("rodent", "Model", function (eventData) {
			var errorData;

			let modelIds = eventData.value.getColumn("Id");
			let modelNames = eventData.value.getColumn("Name");
			let errorModels = eventData.value;
			modelIds.forEach((key, i) => deviceModelMap[key] = modelNames[i]);

			errorModels.filter(["Id", "Name"]);
			errorModels.renameColumn("Name", "text");
			errorModels.renameColumn("Id", "value");
			errorModelDropdown.receiveTextValues(errorModels);

			if (modifyType == "add") {
				errorContainer.setTitle("New Error Code");
				return;
			} else if (modifyType == "edit") {
				errorData = Script.getState("errorData");
			}

			var errorObj = {}
			errorId = errorData.getColumn("id")[0];
			errorObj.CodeId = errorData.getColumn("code id");
			errorObj.Description = errorData.getColumn("description");
			errorObj.Status = errorData.getColumn("status");
			let filteredFile = fileCollection.query((record, pk) => {
				if (record.value == errorData.getColumn("troubleshooting help")[0]) {
					return true;
				}
			});
			errorObj.TroubleshootingHelp = filteredFile.data[0];
			errorObj.CodeType = errorData.getColumn("code type");
			errorObj.OOB = errorData.getColumn("out of bounds");
			//errorObj.ModelId = errorData.getColumn("model id")[0];
			Script.setForm("errorcode", errorObj);
			errorModelDropdown.receiveValue(deviceModelMap[errorData.getColumn("model id")[0]]);
			errorContainer.setTitle("Edit Error Code");
		});
    });
});

ClientEvents.subscribe("finishSubmitError", function() {
	debugger;
    switch (modifyType) {
        case 'edit':
            handleEdit();
            break;
        case 'add':
            handleCreate();
            break;
        case 'edit_file':
            handleEditFile();
            break;
        case 'add_file':
            handleCreateFile();
            break;
    }
});

function handleEdit() {
    var formObj = Script.getFormByKey("errorcode");
    if (formObj == undefined) {
        alert("Please fill out all required fields");
        return;
    }

    var dbReq = {};
    formObj.Id = errorId;
    formObj.Status = +formObj.Status;
    let today = new Date();
