/**
 * Initialise script state (run once at startup)
 */

var modelCollection;
var currentModelType;
var modelTypesCollection;

const modelTypesTable = Script.getWidget("modelsTable");
const compDropdown = Script.getWidget("modelTypesDropdown");

ClientEvents.subscribe("modelTypeSelected", changeModel);
ClientEvents.subscribe("deleteModel", deleteModel);
ClientEvents.subscribe("editModel", editModel);
ClientEvents.subscribe("addModel", addModel);

Script.on('load', function() {
	Database.readRecords("rodent", "ModelType", function(eventData) {
		modelTypesCollection = SensaCollection.load(eventData.value);
		Database.readRecords("rodent", "Model", function(eventData) {
			modelCollection = SensaCollection.load(eventData.value);
			currentModelType = Script.getState("currentModelType");
			let modelTypeNames = modelTypesCollection.getColumn("Name");
			let modelTypeIds = modelTypesCollection.getColumn("Id");
			if (currentModelType == null) {
			  currentModelType = modelTypeIds[0];
			}
			let modelTypeObj = {};
			modelTypeIds.map((model, idx) => {
				modelTypeObj[model] = modelTypeNames[idx];
			});
			Object.keys(modelTypeObj).forEach((model, idx) => {
				modelTypesCollection.set({"Id": model, "Name": modelTypeObj[model]});
			});
		    modelTypesCollection.renameColumn("Name", "text");
			modelTypesCollection.renameColumn("Id", "value");
			compDropdown.receiveTextValues(modelTypesCollection);
			compDropdown.receiveValue(modelTypeObj[currentModelType]);
		    modelTypesCollection.renameColumn("text", "Name");
			modelTypesCollection.renameColumn("value", "Id");
			updateTable();
		});
	});
});

function updateTable() {
      let filteredModels = modelCollection.query((record, pk) => {
          if ((record['ModelType'] == currentModelType) &&
              (record['Status'] == '1')){
              return true;
          }
      });
	  let modelTypeName = modelTypesCollection.query((modelType, pk) => {
		  if (modelType['Id'] == currentModelType){
			  return true;
		  }
	  }).getFirst();
	  filteredModels.forEach((model, pk) => {
          filteredModels.set({"Id": model['Id'], "ModelType": modelTypeName['Name']});
      });
	//name,model type,battery upper,battery lower
      ClientEvents.publish("userDelModels", "");
	  filteredModels.renameColumn("ModelType", "Model Type");
	  filteredModels.renameColumn("BatteryUpper", "Battery Upper");
	  filteredModels.renameColumn("BatteryLower", "Battery Lower");
      modelTypesTable.receiveValue(filteredModels);
}

function changeModel(selected) {
	currentModelType = selected.value;
    updateTable();
}

function deleteModel() {
     let formData = Script.getFormByKey("models");
     var ids = formData.models.getColumn("id");


	if(ids.length === 0) return;

	var option = {"confirmText": "Confirm", "cancelText": "Cancel"};
	Client.confirm("Do you want to delete device model?", "Delete Device Model", option).then((eventData) => {

		//console.log("Return data is  is " + JSON.stringify(eventData, null, 4));

		if(!eventData) return;
		for (var i = 0; i < ids.length; i++) {
          modelTypesTable.deleteRows(ids[i]);
          Database.deleteRecord("rodent", "Model", "Id", ids[i]);
		 }
	});
}

function editModel(eventData) {
    Script.setState("modelInfo", eventData.value);
    Script.setState("currentModelType", currentModelType);
    Script.setState("modifyType", "edit");
    Client.jumpToScreen("Edit Model");
}

 function addModel() {
     Script.setState("currentModelType", currentModelType);
     Script.setState("modifyType", "add");
     Client.jumpToScreen("Edit Model");
 }

