Script.on('load', function() {

	cancelModelBtn.subscribe("pressed", ()=> {
		Client.jumpToScreen("Manage Device Models");
	});
	modifyType = Script.getState("modifyType");
	if (modifyType == "add") {
        modelFormContainer.setTitle("New User Role");
    } else if (modifyType == "edit") {
        modelData = Script.getState("modelInfo");
		modelFormContainer.setTitle("Edit User Role");
		if(modelData != null){
			var modelObj = {};
			modelObj.Name = modelData.getColumn("name");
			modelObj.BatteryLower = modelData.getColumn("batterylower");
			modelObj.BatteryUpper = modelData.getColumn("batteryupper");
			Script.setForm("modelForm", modelObj);
		}
    }
	Database.readRecords("rodent", "ModelType", function(eventData) {
		let modelTypesCollection = SensaCollection.load(eventData.value);
		let modelTypeNames = modelTypesCollection.getColumn("Name");
		let modelTypeIds = modelTypesCollection.getColumn("Id");
		modelTypeId = Script.getState("currentModelType");
		if (modelTypeId == null) {
			modelTypeId = compIds[0];
		}
		let modelTypeObj = {};
		 modelTypeIds.map((model, idx) => {
			 modelTypeObj[model] = modelTypeNames[idx];
		 });
		 Object.keys(modelTypeObj).forEach((model, idx) => {
			 modelTypesCollection.set({"Id": model, "Name": modelTypeObj[model]});
		 });
		 modelTypesCollection.renameColumn("Name", "text");
		 modelTypesCollection.renameColumn("Id", "value");
		 modelTypeSelect.receiveTextValues(modelTypesCollection);
		 modelTypeSelect.receiveValue(modelTypesCollection.get(modelTypeId).text);
	});
});

