	switch (modifyType) {
		case 'edit':
			handleEdit();
			break;
		case 'add':
			handleCreate();
			break;
	}
});

function handleEdit() {
	var formObj = Script.getFormByKey("modelForm");
	if (formObj == undefined) {
		alert("Please fill out all required fields");
		return;
	}
	var dbReq = {};
	let today = new Date();
	today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
	formObj.UpdatedOn = today;
	formObj.UpdatedBy = Client.getUser();
	let modelData = Script.getState("modelInfo");
    let modelId = modelData.getColumn("id")[0];
	formObj.Id = modelId;
	dbReq[modelId] = formObj;
	Database.updateRecord("rodent", "Model", dbReq, () => {
		Script.setState("currentModelType", formObj.ModelType);
		Client.jumpToScreen("Manage Device Models");
    });
}

function handleCreate() {
	Database.readRecords("rodent", "Model", function(eventData) {
		let modelCollection = SensaCollection.load(eventData.value);
		var formObj = Script.getFormByKey("modelForm");
		if (formObj == undefined) {
			alert("Please fill out all required fields");
			return;
		}
		var dbReq = {};
		let today = new Date();
		today = Utils.formatDate(today, "dd/MM/yyyy HH:mm", false);
		let id = +modelCollection.getColumn("Id").at(-1) + 1;
		formObj.Id = `${id}`;
		formObj.CreatedOn = today;
		formObj.UpdatedOn = today;
		formObj.CreatedBy = Client.getUser();
        formObj.UpdatedBy = Client.getUser();
		dbReq[id] = formObj;
		Database.createRecords("rodent", "Model", dbReq, () => {
		Script.setState("currentModelType", formObj.ModelType);
		Client.jumpToScreen("Manage Device Models");
         });
	});
}

