/*Delay function*/
function delay(time) {
    return new Promise(resolve => setTimeout(resolve, time));
}

/**
* Update data in the table
*/
function updateTable(table, collection) {
    ClientEvents.publish("deleteRows", "");
    table.receiveValue(collection);

}

//date time,device number,p5 total,irleft,irright,average,mindelta,maxdelta,l1,l2,range,rebait range,takeng,location,site
/**
* Format time from milisecond to Date and time format
*/
function formatTime(utcTime) {
    var date = new Date(parseInt(utcTime)).toLocaleString('en-GB');
    var d = date.split(", ");
    var d0 = d[0].split("/");
    d0[2] = Math.abs(d0[2] - 2000);
    d0 = d0.join("/");
    return d[1] + " " + d0;
}

