/**
 * Description: 
 * Create Author/Date: 
 * Modified Author/Date Date: 
 * Version: 
 */

/**
 * Initialise script state (run once at startup)
 */
 var userRolesCollection;
 var currentCompany;

 const userRolesTable = Script.getWidget("userRolesTable");
 const compDropdown = Script.getWidget("usersCompanyDropdown");

 Script.on('load', function() {
     Database.readRecords("rodent", "Companies", function(eventData) {
         let companyCollection = SensaCollection.load(eventData.value);

        console.log("Companies are " + JSON.stringify(companyCollection, null, 4));
          Database.readRecords("rodent", "UserRoles", function(eventData) {
              userRolesCollection = SensaCollection.load(eventData.value);
              let filteredUsers = userRolesCollection.query((record, pk) => {
                  if (record['User'] == Client.getUser()) {
                      return true;
                  }
              });
              var userCompanies = filteredUsers.getColumn("CompanyId");

              //var filteredCompanies = userCompanies;

              let filteredCompanies = companyCollection.query((record, pk) => {
                  //if (userCompanies.includes(pk)) {
                      return true;
                  //}
			  });

              let compNames = filteredCompanies.getColumn("Name");
              let compLoc = filteredCompanies.getColumn("Address");
              let compIds = filteredCompanies.getColumn("Id");

              currentCompany = Script.getState("currentCompany");
              if (currentCompany == null) {
                  currentCompany = compIds[0];
              }

              let compObj = {};
              compIds.map((comp, idx) => {
                  compObj[comp] = compNames[idx] + ", " + compLoc[idx];
              });

              Object.keys(compObj).forEach((comp, idx) => {
                  filteredCompanies.set({"Id": comp, "Name": compObj[comp]});
              });

              filteredCompanies.renameColumn("Name", "text");
              filteredCompanies.renameColumn("Id", "value");

              compDropdown.receiveTextValues(filteredCompanies);
              compDropdown.receiveValue(compObj[currentCompany]);

              updateTable();
          });
     });
 });

 ClientEvents.subscribe("usrCcompSelected", changeCompany);
 ClientEvents.subscribe("userEdit", editUser);
 ClientEvents.subscribe("newUser", createNewUser);
 ClientEvents.subscribe("deleteUsers", deleteUsers);

 async function changeCompany(selected) {
      currentCompany = selected.value;

	 Database.readRecords("rodent", "UserRoles", function(eventData) {
          userRolesCollection = SensaCollection.load(eventData.value);
          updateTable();
      });
 }

 function updateTable() {
      let filteredUsers = userRolesCollection.query((record, pk) => {
          if ((record['CompanyId'] == currentCompany) &&
              (record['Status'] == '1')){
              return true;
          }
      });

	 var tableCollection = filteredUsers.filter(["Id", "User", "UpdatedOn", "UpdatedBy", "IsAdmin", "IsPrimary"]);
	 tableCollection.setColumns(["Id", "User", "Updated On", "Updated By", "Is Admin", "Is Primary"]);

      ClientEvents.publish("usrDelRows", "");
      userRolesTable.receiveValue(tableCollection);
 }

 function editUser(eventData) {
     Script.setState("userRoleInfo", eventData.value);
	 //console.log("User event data " + JSON.stringify(eventData.value, null, 4));
     Script.setState("companyId", currentCompany);

	 //console.log("User company " + currentCompany);
     Script.setState("modifyType", "edit");
     Client.jumpToScreen("Edit User Role");
 }

 function createNewUser() {
     Script.setState("companyId", currentCompany);
     Script.setState("modifyType", "add");
     Client.jumpToScreen("Edit User Role");
 }

 function deleteUsers() {
     let formData = Script.getFormByKey("roles");
     var ids = formData.users.getColumn("id");

	 if(ids.length === 0) return;
	 var option = {"confirmText": "Confirm", "cancelText": "Cancel"};
	 Client.confirm("Do you want to delete user role?", "Delete User Roles", option).then((eventData) => {

		//console.log("Return data is  is " + JSON.stringify(eventData, null, 4));

		if(!eventData) return;
		// your code here
		for (var i = 0; i < ids.length; i++) {
          userRolesTable.deleteRows(ids[i]);
          Database.deleteRecord("rodent", "UserRoles", "Id", ids[i]);
       }
	 });

 }
